pragma solidity 0.8.9;

contract Order {

    // Declare state variables of the contract
    address public owner;
    // This is how we associate the food balance with a user's Ethereum account address
    mapping (address => uint) public foodBalances;

    // When 'Order' contract is deployed:
    // 1. set the deploying address as the owner of the contract
    // 2. set the deployed smart contract's food balance to 250
    constructor() {
        // this sets the owner as the original address that deployed the contract because the constructor only gets called when the contract is first created
        owner = msg.sender;
        foodBalances[address(this)] = 250;
    }

    // Allow the owner to increase the smart contract's food balance
    function increaseBalance(uint amount) public {
        require(msg.sender == owner, "Only the owner can increase balanace.");
        foodBalances[address(this)] += amount;
    }

    // Allow anyone to purchase food
    function purchase(uint amount) public payable {
        require(msg.value >= amount * 1 ether, "You must pay at least 1 ETH per food thing.");
        require(foodBalances[address(this)] >= amount, "Not enough food in stock to complete this purchase");
        foodBalances[address(this)] -= amount;
        foodBalances[msg.sender] += amount;
    }
}
